        {name: 'name3',artist: 'artist3',album: 'album3',id: 3}

      ],
      playlistName: 'My Playlist',
      playlistTracks: [
        {name: 'platlistName1',artist: 'playlistArtist1',album: 'playlistAlbum1',id: 4},
        {name: 'platlistName2',artist: 'playlistArtist2',album: 'playlistAlbum2',id: 5},
        {name: 'platlistName3',artist: 'playlistArtist3',album: 'playlistAlbum3',id: 6}
      ]

    }
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
  }

